
// src/views/EntradaVentas/GenerarFactura.jsx
import React, { useState } from 'react'
import { Button, Modal } from 'react-bootstrap'
import jsPDF from 'jspdf'
import autoTable from 'jspdf-autotable'

const GenerarFactura = ({ venta, index, ventasRegistradas, setVentasRegistradas }) => {
  const [showFactura, setShowFactura] = useState(false)
  const [pdfPreview, setPdfPreview] = useState(null)

  const generarFactura = () => {
    const doc = new jsPDF()

    // Logo de la editorial (debes reemplazar con tu logo en Base64)
    const logoBase64 = "data:image/png;base64, ";

    // Logo de la editorial
    doc.addImage(logoBase64, "PNG", 14, 10, 25, 25);

    // Encabezado
    doc.setFontSize(16);
    doc.setFont("helvetica", "bold");
    doc.text("Editorial Guaymuras", 45, 18);
    
    doc.setFontSize(10);
    doc.setFont("helvetica", "normal");
    doc.text("Dirección: Barrio La Plazuela, avenida Paz Barahona,", 45, 26);
    doc.text("Edificio Barrientos, Calle al Arbolito, Centro de Tegucigalpa.", 45, 31);
    doc.text("Tel: +504 3367-3941 | Email: libreriaguaymuras@yahoo.com / info@guaymuras.hn", 45, 36);

    // Datos de factura (derecha)
    doc.setFontSize(10);
    doc.text(`Factura: FAC-${String(index + 1).padStart(4, "0")}`, 200, 15, { align: "right" });
    doc.text(`Fecha: ${venta.fecha}`, 200, 20, { align: "right" });
    doc.text(`Cliente: ${venta.cliente}`, 200, 25, { align: "right" });
    doc.text(`Tipo de Pago: ${venta.tipoPago}`, 200, 30, { align: "right" });

    // Tabla de productos
    const tableData = venta.productos.map((p) => [
      p.cantidad,
      p.libro,
      (Number(p.precio) || 0).toFixed(2),
      (((Number(p.precio) || 0) * (Number(p.cantidad) || 0)) * 0.15).toFixed(2),
      ((Number(p.precio) || 0) * (Number(p.cantidad) || 0)).toFixed(2)
    ])

    autoTable(doc, {
      startY: 42,
      head: [["Cantidad", "Descripción", "Precio", "IVA", "Total"]],
      body: tableData,
      theme: "striped",
      headStyles: { fillColor: [188, 38, 57] },
      styles: { halign: "center", fontSize: 10 }
    });

    // Totales
    let finalY = doc.lastAutoTable.finalY + 10;
    doc.setFont("helvetica", "bold");
    doc.text(`Subtotal: L ${venta.subtotal}`, 190, finalY, { align: "right" });
    doc.text(`Descuento: L ${venta.descuento || 0}`, 190, finalY + 5, { align: "right" });
    doc.text(`Impuesto: L ${venta.impuesto}`, 190, finalY + 10, { align: "right" });
    doc.text(`Total: L ${venta.total}`, 190, finalY + 15, { align: "right" });

    // Generar Blob y mostrar vista previa
    const pdfBlob = doc.output('blob')
    const pdfURL = URL.createObjectURL(pdfBlob)
    setPdfPreview(pdfURL)
    setShowFactura(true)

    // Actualizar historial
    const ventasActualizadas = [...ventasRegistradas]
    ventasActualizadas[index].estadoFactura = 'Emitida'
    ventasActualizadas[index].pdfURL = pdfURL
    setVentasRegistradas(ventasActualizadas)
  }

  const handleVerFactura = () => {
    setPdfPreview(venta.pdfURL)
    setShowFactura(true)
  }

  return (
    <>
      {venta.estadoFactura === 'Pendiente' ? (
        <Button
          size="sm"
          variant="success"
          className="me-1"
          onClick={generarFactura}
        >
          Generar Factura
        </Button>
      ) : (
        <Button
          size="sm"
          variant="success"
          className="me-1"
          onClick={handleVerFactura}
        >
          Ver Factura
        </Button>
      )}

      {/* Modal vista previa de factura */}
      <Modal show={showFactura} onHide={() => setShowFactura(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Factura {venta.estadoFactura === 'Pendiente' ? 'Generada' : 'Emitida'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {pdfPreview && (
            <iframe
              src={pdfPreview}
              title="Vista previa factura"
              style={{ width: '100%', height: '500px' }}
            />
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowFactura(false)}>
            Cerrar
          </Button>
          {pdfPreview && (
            <Button 
              variant="primary" 
              onClick={() => {
                const link = document.createElement('a')
                link.href = pdfPreview
                link.download = `factura-${venta.noFactura || `FAC-${String(index + 1).padStart(4, "0")}`}.pdf`
                link.click()
              }}
            >
              Descargar PDF
            </Button>
          )}
        </Modal.Footer>
      </Modal>
    </>
  )
}

export default GenerarFactura